// --- CONFIGURATION ---
const CUTOFF_SCORE = 70;
// ---------------------

const allowedKeys = [
  "candidate_name",
  "email",
  "phone",
  "location",
  "ats_score",
  "status",
  "summary",
  "missing_skills",
  "reasoning",
];

for (const item of $input.all()) {
  let aiData = {};
  // 1. NAVIGATE TO THE CORRECT DATA PATH
  // Based on your screenshot, the data is at: content -> content[0] -> text
  try {
    if (
      item.json.output &&
      item.json.output.length > 0 &&
      item.json.output[0].content &&
      item.json.output[0].content.length > 0 &&
      item.json.output[0].content[0] &&
      item.json.output[0].content[0].text
    ) {
      aiData = item.json.output[0].content[0].text;
    }
    // Fallback: Check if it's directly at the root (just in case)
    else if (item.json.ats_score !== undefined) {
      aiData = item.json;
    }
  } catch (error) {
    aiData = { reasoning: "Error navigating JSON structure: " + error.message };
  }

  // 2. CALCULATE STATUS
  // Use '0' if score is missing
  const score = aiData.ats_score || 0;

  if (score >= CUTOFF_SCORE) {
    aiData.status = "Qualified";
  } else {
    aiData.status = "Rejected";
  }

  // 3. FILTER & REBUILD OUTPUT
  const filteredJson = {};
  for (const key of allowedKeys) {
    // Check aiData (the found object), not item.json
    if (aiData.hasOwnProperty(key)) {
      filteredJson[key] = aiData[key];
    } else {
      filteredJson[key] = null;
    }
  }

  // 4. Generate Unique ID
  // Generate a simple random ID (e.g., Applicant_8392)
  const uniqueId = "Applicant_" + Math.floor(Math.random() * 10000);
  filteredJson["Unique_Id"] = uniqueId

  // Update the item
  item.json = filteredJson;
}

return $input.all();

// Extract just the Job Titles into a simple array
const roles = $input.all().map(item => item.json.Job_Title);

// Return it as a JSON object
return { json: { roles: roles } };

